import React from 'react';
import { connect } from 'react-redux'
import axios from 'axios'
import { MDBNavbar, MDBInput, MDBBtn, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, MDBCollapse } from "mdbreact";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'
import './App.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            content: [],
            sort_name: 'none',
            sort_author: 'none',
            genre: 'none',
            genre1: [],
            gender: 'none'
        };
        this.handleChange = this.handleChange.bind(this)
        this.cleanArray = this.cleanArray.bind(this)
        this.search = this.search.bind(this)
        this.handleChange1 = this.handleChange1.bind(this)
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal,
        });
    }

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value })
        this.search()
    }

    handleChange1(e) {
        this.setState({ genre: e.target.value })
    }

    componentDidMount() {
        axios.get("http://localhost:8080/data.json").then(res => {
            this.setState({ content: res.data })
            console.log('test', this.state.content);
            var table = []
            for (let i = 0; i < res.data.length; i++) {
                table.push(res.data[i].genre)
            }
            var t1 = this.cleanArray(table)
            this.setState({ genre1: t1 })
        })

    }

    cleanArray(array) {
        var i, j, len = array.length, out = [], obj = {};
        for (i = 0; i < len; i++) {
            obj[array[i]] = 0;
        }
        for (j in obj) {
            out.push(j);
        }
        return out;
    }

    search() {
        axios.get("http://localhost:8080/data.json").then(res => {
            var test = []
            for (let i = 0; i < res.data.length; i++) {
                test.push(res.data[i])
            }
            console.log('test:', test);

            //DUAL FILTER
            if (this.state.genre == 'none' && this.state.gender == 'none') {
                this.setState({ content: test })
            } else if (this.state.genre !== 'none' && this.state.gender == 'none') {
                var result = test.filter(book => book.genre == this.state.genre)
                console.log('resultat genre:', result);
                this.setState({ content: result })
            } else if (this.state.genre == 'none' && this.state.gender !== 'none') {
                var results = test.filter(book => book.author.gender == this.state.gender)
                console.log('resultat gender:', results);
                this.setState({ content: results })
            } else {

            }


            //FILTER BY BOOK GENRE
            if (this.state.genre !== 'none') {
                document.getElementById('gender').style.display = 'none'
                var result = test.filter(book => book.genre == this.state.genre)
                console.log('resultat tab:', result);

                this.setState({ content: result })

            } else {
                document.getElementById('gender').style.display = 'block'
                this.setState({ content: test })
            }


            //FILTER BY BOOK GENDER
            if (this.state.gender !== 'none') {
                document.getElementById('genre').style.display = 'none'
                var results = test.filter(book => book.author.gender == this.state.gender)
                console.log('resultat tab:', results);

                this.setState({ content: results })
            } else {
                document.getElementById('genre').style.display = 'block'
                this.setState({ content: test })
            }

            //SORT BY BOOK NAME
            if (this.state.sort_name !== 'none') {
                document.getElementById('sort_author').style.display = 'none'
                if (this.state.sort_name == 'Z') {
                    var result1 = this.state.content.sort((a, b) => {
                        var x = a.name.toLowerCase()
                        var y = b.name.toLowerCase()
                        if (x < y) { return 1 } else { return -1 }
                    })
                    this.setState({ content: result1 })
                    console.log('test sort by book name: (-)', this.state.content);
                } else {
                    var result1 = this.state.content.sort((a, b) => {
                        var x = a.name.toLowerCase()
                        var y = b.name.toLowerCase()
                        if (x < y) { return -1 } else { return 1 }
                    })
                    this.setState({ content: result1 })
                    console.log('test sort by book name: (+)', this.state.content);
                }
            } else {
                document.getElementById('sort_author').style.display = 'block'
            }


            //SORT BY AUTHOR NAME
            if (this.state.sort_author !== 'none') {
                document.getElementById('sort_name').style.display = 'none'
                if (this.state.sort_author == 'Z') {
                    var result2 = this.state.content.sort((a, b) => {
                        var x = a.author.name_author.toLowerCase()
                        var y = b.author.name_author.toLowerCase()
                        if (x < y) { return 1 } else { return -1 }
                    })
                    this.setState({ content: result2 })
                    console.log('test sort by author name:', this.state.content);
                } else {
                    var result2 = this.state.content.sort((a, b) => {
                        var x = a.author.name_author.toLowerCase()
                        var y = b.author.name_author.toLowerCase()
                        if (x < y) { return -1 } else { return 1 }
                    })
                    this.setState({ content: result2 })
                    console.log('test sort by author name:', this.state.content);
                }
            } else {
                document.getElementById('sort_name').style.display = 'block'
            }

        })
    }

    render() {
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <div className="col-md-2"></div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select on={this.search} name="sort_name" class="custom-select" id="sort_name" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Sort by Book Name</option>
                                    <option value="A">from A to Z</option>
                                    <option value='Z'>from Z to A</option>
                                </select>
                            </div>
                        </div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select name="sort_author" class="custom-select" id="sort_author" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Sort by Author Name</option>
                                    <option value="A">from A to Z</option>
                                    <option value='Z'>from Z to A</option>
                                </select>
                            </div>
                        </div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select name='genre' class="custom-select" id="genre" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Book genre</option>
                                    {this.state.genre1.map((book) => (
                                        <option>{book}</option>
                                    ))}
                                </select>
                            </div>
                        </div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select name="gender" class="custom-select" id="gender" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Author gender</option>
                                    <option value="M">Men</option>
                                    <option value='F'>Women</option>
                                </select>
                            </div>
                        </div>

                    </div>
                </div>

                <div className='content'>
                    <div className='row'>
                        <div className='col-md-2'></div>
                        <div className='col-md-8'>
                            <table className="table table-bordered table-hover">
                                <thead className=" thead-dark">
                                    <tr className="tab">
                                        <th>Book Name</th>
                                        <th>Author</th>
                                        <th>Book Genre</th>
                                        <th>Publish date</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.content.map((book) => (
                                        <tr>
                                            <td>{book.name}</td>
                                            <td>{(book.author.gender == 'M') ? "Mister" : "Miss"} {book.author.name_author}</td>
                                            <td>{book.genre}</td>
                                            <td>{book.date}</td>
                                        </tr>
                                    )
                                    )}
                                </tbody>
                            </table>
                        </div>
                        <div className='col-md-2'></div>
                    </div>
                </div>
            </div>
        )
    }
}


