import React from 'react';
import { connect } from 'react-redux'
import axios from 'axios'
import { MDBNavbar, MDBInput, MDBBtn, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, MDBCollapse } from "mdbreact";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'
import './App.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            content: [],
            genre: ''
        };
        this.handleChange = this.handleChange.bind(this)
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal,
        });
    }

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value })
    }

    componentDidMount() {
        axios.get("http://localhost:8080/data.json").then(res => {
            this.setState({ content: res.data })
            console.log('test', this.state.content);
        })
    }

    componentDidUpdate() {
        console.log('genre: ', this.state.genre);

        //     axios.get("https://tsiorytahback.herokuapp.com/profil").then(res => {
        //         // axios.get("http://localhost:8080/profil").then(res => {
        //         var tab = []
        //         for (let i = 0; i < res.data.length; i++) {
        //             if (res.data[i].active == true && res.data[i].reserve < res.data[i].disponible) {
        //                 tab.push(res.data[i])
        //             }
        //         }
        //         this.setState({ comment: tab })
        //     })
    }

    render() {
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <center>
                            <div className="col-md-2"></div>
                            <div className="col-md-2">
                                <div class="input-group mb-3">
                                    <select name='genre' class="custom-select" id="inputGroupSelect01" value={this.state.value} onChange={this.handleChange}>
                                        <option selected>Book genre</option>
                                        {this.state.content.map((book) => (
                                            <option value={this.state.genre}>{book.genre}</option>
                                        ))}
                                    </select>
                                </div>
                            </div>
                            <div className="col-md-2">
                                <div class="input-group mb-3">
                                    <select class="custom-select" id="inputGroupSelect01">
                                        <option selected>Author gender</option>
                                        {this.state.content.map((book) => (
                                            <option>{book.author.gender}</option>
                                        ))}
                                    </select>
                                </div>
                            </div>
                            <div className="content">
                            </div>
                        </center>
                    </div>
                </div>

                <div className='container'>
                    <div className='row'>
                        <div className='col-md-2'></div>
                        <div className='col-md-8'>
                            <table className="table table-bordered table-hover">
                                <thead className=" thead-dark">
                                    <tr className="tab">
                                        <th>Book Name</th>
                                        <th>Author</th>
                                        <th>Book Genre</th>
                                        <th>Publish date</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.content.sort().reverse().map((book) => (
                                        <tr>
                                            <td>{book.name}</td>
                                            <td>{(book.author.gender == 'M') ? "Mister" : "Miss"} {book.author.name_author}</td>
                                            <td>{book.genre}</td>
                                            <td>{book.date}</td>
                                        </tr>
                                    )
                                    )}
                                </tbody>
                            </table>
                        </div>
                        <div className='col-md-2'></div>
                    </div>
                </div>
            </div>
        )
    }
}


