import React from 'react';
import { connect } from 'react-redux'
import axios from 'axios'
import { MDBNavbar, MDBInput, MDBBtn, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, MDBCollapse } from "mdbreact";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            content: [],
            genre: []
        };
        this.handleChange = this.handleChange.bind(this)
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal,
        });
    }

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value })
    }

    componentDidMount() {
        axios.get("http://localhost:8080/data.json").then(res => {
            this.setState({ content: res.data })
            console.log('test', this.state.content);
            var genre = []
            for (let i=0; i<this.state.content.length; i++){
                genre.push(this.state.content[i].genre)
            }
            this.setState({genre: genre})
            console.log('genres:', this.state.genre);

        })
    }

    // componentDidUpdate() {
    //     axios.get("https://tsiorytahback.herokuapp.com/profil").then(res => {
    //         // axios.get("http://localhost:8080/profil").then(res => {
    //         var tab = []
    //         for (let i = 0; i < res.data.length; i++) {
    //             if (res.data[i].active == true && res.data[i].reserve < res.data[i].disponible) {
    //                 tab.push(res.data[i])
    //             }
    //         }
    //         this.setState({ comment: tab })
    //     })
    // }

    render() {
        return (
            <div className="container">
                <br/><br/><br/><div className="row">
                    <div className="col-md-3"></div>
                    <div className="col-md-6">
                        <div class="input-group mb-3">
                            <div class="input-group-prepend">
                                <label class="input-group-text" for="inputGroupSelect01">Name</label>
                            </div>
                            <select class="custom-select" id="inputGroupSelect01">
                                <option selected>Name</option>
                                <option value="1">One</option>
                                <option value="2">Two</option>
                                <option value="3">Three</option>
                            </select>
                        </div>
                        <div class="input-group mb-3">
                            <div class="input-group-prepend">
                                <label class="input-group-text" for="inputGroupSelect01">Author</label>
                            </div>
                            <select class="custom-select" id="inputGroupSelect01">
                                <option selected>Author</option>
                                <option value="1">One</option>
                                <option value="2">Two</option>
                                <option value="3">Three</option>
                            </select>
                        </div>
                        <div class="input-group mb-3">
                            <div class="input-group-prepend">
                                <label class="input-group-text" for="inputGroupSelect01">Date</label>
                            </div>
                            <select class="custom-select" id="inputGroupSelect01">
                                <option selected>Date</option>
                                <option value="1">One</option>
                                <option value="2">Two</option>
                                <option value="3">Three</option>
                            </select>
                        </div>
                    </div>
                    <div className="col-md-3"></div>

                </div>
            </div>
        )
    }
}


