import React from 'react';
import { connect } from 'react-redux'
import axios from 'axios'
import { MDBNavbar, MDBInput, MDBBtn, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, MDBCollapse } from "mdbreact";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'
import './App.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            content: [],
            sort_name: '',
            sort_author: '',
            genre: '',
            gender: ''
        };
        this.handleChange = this.handleChange.bind(this)
        this.handleChange1 = this.handleChange1.bind(this)
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal,
        });
    }

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value })
    }

    handleChange1(e) {
        this.setState({ genre: e.target.value })
    }

    componentDidMount() {
        axios.get("http://localhost:8080/data.json").then(res => {
            this.setState({ content: res.data })
            console.log('test', this.state.content);
        })
    }

    componentDidUpdate() {
        console.log(' genre: ', this.state.genre, ' gender: ', this.state.gender);
        console.log('sort_name:', this.state.sort_name, ' sort_author:', this.state.sort_author);
        axios.get("http://localhost:8080/data.json").then(res => {
            if(this.state.sort_name == 'Z'){
                var test = []
                test.push(res.data.sort((a, b) => {
                    var x = a.name.toLowerCase()
                    var y = b.name.toLowerCase()
                    if (x<y) {return 1} else {return -1}
                }))
                this.setState({content: test})
                console.log('test', this.state.content);
            }
        })
    }

    render() {
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <div className="col-md-2"></div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select name="sort_name" class="custom-select" id="sort_name" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Sort by Book Name</option>
                                    <option value="A">from A to Z</option>
                                    <option value='Z'>from Z to A</option>
                                </select>
                            </div>
                        </div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select name="sort_author" class="custom-select" id="sort_author" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Sort by Author Name</option>
                                    <option value="A">from A to Z</option>
                                    <option value='Z'>from Z to A</option>
                                </select>
                            </div>
                        </div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select name="gender" class="custom-select" id="gender" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Author gender</option>
                                    <option value="M">Man</option>
                                    <option value='F'>Woman</option>
                                </select>
                            </div>
                        </div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select name='genre' class="custom-select" id="genre" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Book genre</option>
                                    {this.state.content.map((book) => (
                                        <option>{book.genre}</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                    </div>
                </div>

                <div className='content'>
                    <div className='row'>
                        <div className='col-md-2'></div>
                        <div className='col-md-8'>
                            <table className="table table-bordered table-hover">
                                <thead className=" thead-dark">
                                    <tr className="tab">
                                        <th>Book Name</th>
                                        <th>Author</th>
                                        <th>Book Genre</th>
                                        <th>Publish date</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.content.map((book) => (
                                        <tr>
                                            <td>{book.name}</td>
                                            <td>{(book.author.gender == 'M') ? "Mister" : "Miss"} {book.author.name_author}</td>
                                            <td>{book.genre}</td>
                                            <td>{book.date}</td>
                                        </tr>
                                    )
                                    )}
                                </tbody>
                            </table>
                        </div>
                        <div className='col-md-2'></div>
                    </div>
                </div>
            </div>
        )
    }
}


