import React from 'react';
import { connect } from 'react-redux'
import axios from 'axios'
import { MDBNavbar, MDBInput, MDBBtn, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, MDBCollapse } from "mdbreact";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'
import './App.css'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            content: [],
            sort_name: '',
            sort_author: '',
            genre: '',
            gender: ''
        };
        this.handleChange = this.handleChange.bind(this)
        this.search = this.search.bind(this)
        this.handleChange1 = this.handleChange1.bind(this)
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal,
        });
    }

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value })
        this.search()
    }

    handleChange1(e) {
        this.setState({ genre: e.target.value })
    }

    componentDidMount() {
        axios.get("http://localhost:8080/data.json").then(res => {
            this.setState({ content: res.data })
            console.log('test', this.state.content);
        })
    }

     cleanArray(array) {
        var i, j, len = array.length, out = [], obj = {};
        for (i = 0; i < len; i++) {
            obj[array[i]] = 0;
        }
        for (j in obj) {
            out.push(j);
        }
        return out;
    }

    search() {
        axios.get("http://localhost:8080/data.json").then(res => {
            var test = []
            for (let i = 0; i < res.data.length; i++) {
                test.push(res.data[i])
            }
            //SORT BY BOOK NAME
            // if (this.state.sort_name == 'Z') {
