            //         var x = a.name.toLowerCase()
            //         var y = b.name.toLowerCase()
            //         if (x < y) { return 1 } else { return -1 }
            //     })
            //     this.setState({ content: test })
            // } else {
            //     test.sort((a, b) => {
            //         var x = a.name.toLowerCase()
            //         var y = b.name.toLowerCase()
            //         if (x < y) { return -1 } else { return 1 }
            //     })
            //     this.setState({ content: test })
            // }

            //SORT BY AUTHOR NAME
            // if (this.state.sort_author == 'Z') {
            //     test.sort((a, b) => {
            //         var x = a.author.name_author.toLowerCase()
            //         var y = b.author.name_author.toLowerCase()
            //         if (x < y) { return 1 } else { return -1 }
            //     })
            //     this.setState({ content: test })
            // } else {
            //     test.sort((a, b) => {
            //         var x = a.author.name_author.toLowerCase()
            //         var y = b.author.name_author.toLowerCase()
            //         if (x < y) { return -1 } else { return 1 }
            //     })
            //     this.setState({ content: test })
            // }

            //FILTER BY BOOK GENRE
        })
    }

    render() {
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <div className="col-md-2"></div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select on={this.search} name="sort_name" class="custom-select" id="sort_name" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Sort by Book Name</option>
                                    <option value="A">from A to Z</option>
                                    <option value='Z'>from Z to A</option>
                                </select>
                            </div>
                        </div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select name="sort_author" class="custom-select" id="sort_author" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Sort by Author Name</option>
                                    <option value="A">from A to Z</option>
                                    <option value='Z'>from Z to A</option>
                                </select>
                            </div>
                        </div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select name='genre' class="custom-select" id="genre" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Book genre</option>
                                    {
                                        this.state.content.map((book) => (
                                        <option>{book.genre}</option>
                                    ))}
                                </select>
                            </div>
                        </div>

                        <div className="col-md-2">
                            <div class="input-group mb-3">
                                <select name="gender" class="custom-select" id="gender" value={this.state.value} onChange={this.handleChange}>
                                    <option value='none' selected>Author gender</option>
                                    <option value="M">Men</option>
                                    <option value='F'>Women</option>
                                </select>
                            </div>
                        </div>

                    </div>
                </div>

                <div className='content'>
                    <div className='row'>
                        <div className='col-md-2'></div>
                        <div className='col-md-8'>
                            <table className="table table-bordered table-hover">
                                <thead className=" thead-dark">
                                    <tr className="tab">
                                        <th>Book Name</th>
                                        <th>Author</th>
                                        <th>Book Genre</th>
                                        <th>Publish date</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.content.map((book) => (
                                        <tr>
                                            <td>{book.name}</td>
                                            <td>{(book.author.gender == 'M') ? "Mister" : "Miss"} {book.author.name_author}</td>
                                            <td>{book.genre}</td>
                                            <td>{book.date}</td>
                                        </tr>
                                    )
                                    )}
                                </tbody>
                            </table>
                        </div>
                        <div className='col-md-2'></div>
                    </div>
                </div>
            </div>
        )
    }
}


